mapper.update:
UPDATE wt_comment
SET content = #{content}
WHERE comment_no = #{commentNo}

mapper.select:
SELECT log_no, webtoon_no, user_no, count
FROM wt_user_log
WHERE webtoon_no = #{webtoonNo}
AND user_no = #{userNo}

SELECT *
FROM wt_user_save
WHERE webtoon_no = #{webtoonNo}
AND user_no = #{userNo}

mapper.update:
UPDATE wt_comment
SET liked = liked + 1
WHERE comment_no = #{commentNo};

mapper.delete:
DELETE FROM wt_comment_like
WHERE comment_no = #{commentNo}
AND user_no = #{userNo};

mapper.update:
UPDATE wt_comment
SET liked = liked - 1
WHERE comment_no = #{commentNo};

UPDATE wt_comment
SET dislike = dislike + 1
WHERE comment_no = #{commentNo};

UPDATE wt_comment
SET dislike = dislike - 1
WHERE comment_no = #{commentNo};

mapper.select:
select * from webtoon
ORDER BY count DESC
LIMIT 12;
